import React, { useState, useEffect } from 'react';
import './App.css';
import { io } from 'socket.io-client';
// Initialize the socket connection to the backend
const socket = io('http://localhost:5000');

function App() {
  // States to hold form inputs and messages
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [messageHistory, setMessageHistory] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [isSignedIn, setIsSignedIn] = useState(false);

  // Handle form submission to register a new user
  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = {
      name: username,
      email: email,
      password: password,
    };

    try {
      const response = await fetch('/api/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(user),
      });
      if (!response.ok) {
        throw new Error("Response is not ok");
      }
      const message = await response.json();
      alert(message.message);
      setCurrentUser(user.name);
      setIsSignedIn(true); // Set signed in state here
    } catch (error) {
      console.error("Error:", error);
    }
  };

  // Handle chat message submission
  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    const messageData = {
      user: currentUser,
      text: message,
    };

    socket.emit('chat message', messageData);
    setMessageHistory((prevHistory) => [...prevHistory, messageData]);
    setMessage("");
  };

  // Listen for incoming messages from the server
  useEffect(() => {
    socket.on('receive message', (msg) => {
      setMessageHistory((prevHistory) => [...prevHistory, msg]);
    });

    return () => {
      socket.off('receive message');
    };
  }, []);

  return (
    <div className="app-container">
      {isSignedIn ? (
        <>
          {/* Chat Section */}
          <form onSubmit={sendMessage} className="chat-form">
            <input
              type='text'
              placeholder='Write message...'
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button type="submit">Send Message</button>
          </form>

          <div className='chatWrapper'>
            <h3>Message History:</h3>
            <div className="message-history-container">
              {messageHistory.map((msg, index) => (
                <div
                  key={index}
                  className={`message-item ${msg.user === currentUser ? 'sent' : 'received'}`}
                >
                  <strong>{msg.user === currentUser ? 'You' : msg.user}:</strong> {msg.text}
                </div>
              ))}
            </div>
          </div>
        </>
      ) : (
        <>
          {/* Sign Up Form */}
          <form onSubmit={handleSubmit} className="signup-form">
            <p className='formTitle'>Sign-Up</p>
            <div className='formInputs'>
              <input
                className='credInputs'
                type='text'
                placeholder='Username'
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <input
                className='credInputs'
                type='email'
                placeholder='Email'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                className='credInputs'
                type='password'
                placeholder='Password'
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className='signUpBtn'>Sign Up</button>
          </form>
        </>
      )}
    </div>
  );
}

export default App;
