import React, { useState } from 'react'

const Signup = () => {

    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [gender, setGender] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        const user = {
          name: username,
          email: email,
          password: password,
          gender: gender,
        };

        try {
          const response = await fetch('/api/auth/signup', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(user),
          });
          if (!response.ok) {
            throw new Error("Response is not ok");
          }
          const message = await response.json();
          alert(message.message);
        } catch (error) {
          console.error("Error:", error);
        }
      };

  return (
    <div className='h-screen bg-gradient-to-r from-[#3C0753] to-[#910A67] flex justify-center items-center'>
        <form onSubmit={handleSubmit} className="h-3/5 w-1/4 flex flex-col text-white font-serif">
            <span className='text-center'>Register yourself !</span>
            <div className='flex flex-col '>
              <label>Name</label>
              <input
                className='credInputs'
                type='text'
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <label>Email</label>
              <input
                className='credInputs'
                type='email'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <label>Password</label>
              <input
                className='credInputs'
                type='password'
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <label>Gender</label>
              <input
                className='credInputs'
                type='text'
                value={gender}
                onChange={(e) => setGender(e.target.value)}
                required
              />
            </div>
            <button type="submit" className='signUpBtn'>Sign Up</button>
        </form>
    </div>
  )
}

export default Signup
