import React, { useState, useEffect } from 'react';
import './App.css';
import { io } from 'socket.io-client';

// Initialize the socket connection to the backend
const socket = io('http://localhost:5000');  // Connecting to the backend Socket.io server

function App() {
  // States to hold form inputs and messages
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [messageHistory, setMessageHistory] = useState([]);  // For storing chat history

  // Handle form submission to register a new user
  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = {
      name: username,
      email: email,
      password: password
    };

    try {
      const response = await fetch('/signup', {  // Using full backend URL
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(user)
      });

      if (!response.ok) {
        throw new Error("Response is not ok");
      }

      const message = await response.json();
      console.log(message);
      alert(message.message);  // Show an alert when signup is successful

    } catch (error) {
      console.error("Error:", error);
    }
  };

  // Handle chat message submission
  const sendMessage = (e) => {
    e.preventDefault();
    socket.emit('chat message', message);  // Emit the 'chat message' event with the message data
    setMessage("");  // Clear the input field after sending
  };

  // Listen for incoming messages from the server
  useEffect(() => {
    // Listen for the 'chat message' event
    socket.on('chat message', (msg) => {
      console.log('Received from server:', msg);
      setMessageHistory(prevHistory => [...prevHistory, msg]);  // Append the new message to history
    });

    // Cleanup the event listener when the component unmounts
    return () => {
      socket.off('chat message');
    };
  }, []);

  return (
    <div>
      {/* Sign Up Form */}
      <form onSubmit={handleSubmit}>
        <input
          type='text'
          placeholder='Username'
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type='email'
          placeholder='Email'
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type='password'
          placeholder='Password'
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Sign Up</button>
      </form>

      {/* Chat Section */}
      <form onSubmit={sendMessage}>
        <input
          type='text'
          placeholder='Write message'
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button type="submit">Send Message</button>
      </form>

      {/* Display received chat message history */}
      <div>
        <h3>Message History:</h3>
        <div className="message-history-container">
          {messageHistory.map((msg, index) => (
            <p key={index}>{msg}</p>  // Render each message in the history
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
